        return text;
    //workaround this non-latin symbols
    var htmlValue = new Element('div', {
        'html' : text
    });
    text = htmlValue.get('text');

    // try to cut unnecessary symbols
    if (length != null && text.length > length)
        return text.substr(0, length) + "...";
    else
        return text;
