function arraySplit(source, splitterFn){
    var result = [];
    if (source && source.length) {
        source.each(function (record, idx) {
            var splitIdx = Number(splitterFn(record, idx));
            switch (splitIdx) {
                case -1: break; //exclude
                default:
                    if (!result[splitIdx]) {
                        result[splitIdx] = []
                    }
                    result[splitIdx].push(record);
            }
        });
    }

    return result;
}

