					group.minX - nwOffset.x;
		if (group.maxX != null)
			group.maxMouseX = group.minMouseX + group.maxX - group.minX;

		if (group.minY != null)
			group.minMouseY = mouse.y - nwPosition.y +
					group.minY - nwOffset.y;
