    var nonValid = (((charCode > 64 && charCode < 91) || (charCode >= 186 && charCode < 193) || (charCode >= 219 && charCode <= 222) || charCode == 32 || (charCode >= 106 && charCode <= 111)) && !(event.ctrlKey || event.metaKey) || (event.shiftKey && (charCode >= 48 && charCode <= 57)));

